mapper.select:
select u.id, u.account, u.password, r.role, u.name, u.sfzh, u.nation, u.sex, u.birth, u.phone, u.area, u.file_path as filePath, o.org_code as orgCode, o.org_name as orgName, u.balance, u.status, u.create_time as createTime, u.update_time as updateTime from tb_user u left join tb_role r on u.role_id = r.id left join tb_org o on u.org_id = o.id
u.status = '0'
and u.name like concat('%', #{user.name}, '%')
and u.sfzh = #{user.sfzh}
and u.phone = #{user.phone}
and u.org_code = #{user.orgCode}
and u.org_name = #{user.orgName}
and r.role = #{user.role}

select u.id, u.account, u.password, r.role, u.name, u.sfzh, u.nation, u.sex, u.birth, u.phone, u.area, u.file_path as filePath, o.org_code as orgCode, o.org_name as orgName, u.balance, u.status, u.create_time as createTime, u.update_time as updateTime from tb_user u left join tb_role r on u.role_id = r.id left join tb_org o on u.org_id = o.id
u.status = '1'
and u.name like concat('%', #{user.name}, '%')
and u.sfzh = #{user.sfzh}
and u.phone = #{user.phone}
and u.org_code = #{user.orgCode}
and u.org_name = #{user.orgName}
and r.role = #{user.role}
